mapper.insert:
INSERT INTO moviepay ( moviepay_adult, moviepay_child, moviepay_adultpay, moviepay_childpay, moviepay_price, moviepay_seat, moviepay_paydate, moviepay_point_use, moviepay_point, moviepay_refund, moviepay_viewdate, moviepay_viewtime, movie_no, member_no, moviepay_viewregion
) VALUES ( #{moviepayAdult}, #{moviepayChild}, #{moviepayAdultpay}, #{moviepayChildpay}, #{moviepayPrice}, #{moviepaySeat}, #{moviepayPaydate}, #{moviepayPointUse}, #{moviepayPoint}, #{moviepayRefund}, #{moviepayViewdate}, #{moviepayViewtime}, #{movieNo}, #{memberNo}, #{moviepayViewregion}
)

mapper.select:
SELECT member_point
FROM member
WHERE member_no = #{userId}

SELECT GROUP_CONCAT(moviepay_seat SEPARATOR ',') AS booked_seats
FROM moviepay
WHERE movie_no = #{movieNo, jdbcType=INTEGER}
  AND moviepay_viewdate = #{viewDate, jdbcType=DATE}
  AND moviepay_viewtime = #{time, jdbcType=TIME};
